</head>
<body>
    <div class="chat-container" id="chatModal" style="display: none; position: fixed; top: 50%; left: 50%; transform: translate(-50%, -50%); background-color: white; padding: 20px; z-index: 1000; border-radius: 5px; box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);">
        <div class="chat-header">
            <h2>Yakwetu Chatbot</h2>
            <span class="close-btn" id="closeChatButton">&times;</span>
        </div>
        <div class="chat-body" id="chatBody">
            <!-- Chat messages will be displayed here -->
        </div>
        <div class="chat-input">
            <input type="text" id="userQuery" class="user-input" placeholder="Type your message...">
            <button onclick="sendMessage()" class="send-btn">Send</button>
        </div>
    </div>

    </div>
    <div id="overlay" style="display: none; position: fixed; top: 0; left: 0; width: 100%; height: 100%; background-color: rgba(0, 0, 0, 0.5); z-index: 900;"></div>
    <script>
        const openChatButton = document.getElementById("openChatButton");
        const closeChatButton = document.getElementById("closeChatButton");
        const chatModal = document.getElementById("chatModal");
        const overlay = document.getElementById("overlay");

        openChatButton.addEventListener("click", () => {
            chatModal.style.display = "block";
            overlay.style.display = "block";

			
        });

        closeChatButton.addEventListener("click", () => {
            chatModal.style.display = "none ";
            overlay.style.display = "none ";
        });

		
        async function sendMessage() {
            const userQuery = document.getElementById('userQuery').value;
            const chatBody = document.getElementById('chatBody');

            if (userQuery.trim() !== '') {
                const userMessage = document.createElement('div');
                userMessage.className = 'chat-message user-message';
                userMessage.textContent = userQuery;

                chatBody.appendChild(userMessage);

                const apiUrl = 'https://developers.sarufi.io/conversation';
                const authToken = 'e92cfc338c7c289366cea18cca0285c011a671b4565efd46e7cfd0bec36e1306';
                const requestBody = {
                    chat_id: '1',
                    bot_id: '2012',
                    message: userQuery
                };

                try {
                    const response = await fetch(apiUrl, {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                            'Authorization': `Bearer ${authToken}`
                        },
                        body: JSON.stringify(requestBody),
                    });

                    const data = await response.json();
                    const botResponse = data.actions[0].send_message[0];

                    const botMessage = document.createElement('div');
                    botMessage.className = 'chat-message bot-message';
                    botMessage.textContent = botResponse;

                    chatBody.appendChild(botMessage);
                } catch (error) {
                    console.error('Error:', error);
                }
            }
            document.getElementById('userQuery').value = '';
        }

    </script>
</body>
